mapper.select:
SELECT i.DEPT_CD, e.EMPL_ID
FROM (
         SELECT bd.BLNG_EMPL_NO, d.DEPT_CD, d.DEPT_NM, d.ENG_DEPT_NM
         FROM blng_dept bd
                  INNER JOIN dept d
                             ON bd.BLNG_DEPT_CD = d.DEPT_CD
         WHERE d.DEPT_CD = #{deptCd}
     ) i INNER JOIN empl e ON i.BLNG_EMPL_NO = e.EMPL_NO;
