mapper.insert:
SELECT EMPL_NO FROM empl WHERE EMPL_ID = #{emplId}
INSERT INTO space_book (SPACE_BOOK_ID, SPACE_BOOK_EMPL_NO, SPACE_BOOK_SPACE_NO, SPACE_BOOK_SPACE_NM, SPACE_BOOK_LOC_DESC, SPACE_BOOK_DATE, SPACE_BOOK_BGN_TM, SPACE_BOOK_END_TM, SPACE_BOOK_CN, SPACE_BOOK_STUS_CD, FST_REG_DTM, FST_REGR_IDNF_NO, LAST_UPD_DTM, LAST_UPDR_IDNF_NO) SELECT #{spaceBookId}, #{spaceBookEmplNo}, #{spaceBookSpaceNo}, (SELECT SPACE_NM FROM space WHERE SPACE_NO = #{spaceBookSpaceNo}), (SELECT SPACE_LOC_DESC FROM space WHERE SPACE_NO = #{spaceBookSpaceNo}), #{spaceBookDate}, #{spaceBookBgnTm}, #{spaceBookEndTm}, #{spaceBookCn}, #{spaceBookStusCd}, #{fstRegDtm}, #{spaceBookEmplNo}, #{lastUpdDtm}, #{spaceBookEmplNo} WHERE not exists( SELECT * FROM space_book WHERE DATEDIFF(SPACE_BOOK_DATE, #{spaceBookDate}) = 0 AND SPACE_BOOK_SPACE_NO = #{spaceBookSpaceNo} AND SPACE_BOOK_STUS_CD = #{spaceBookStusCd} AND ( (SPACE_BOOK_END_TM > #{spaceBookBgnTm} AND SPACE_BOOK_BGN_TM < #{spaceBookEndTm})
OR (SPACE_BOOK_END_TM = #{spaceBookBgnTm} AND SPACE_BOOK_EMPL_NO = #{spaceBookEmplNo}) OR (SPACE_BOOK_BGN_TM = #{spaceBookEndTm} AND SPACE_BOOK_EMPL_NO = #{spaceBookEmplNo})
) ) AND (SELECT IF(
NOT(s.SPACE_WKEND_USG_POSBL_YN = 'N' AND WEEKDAY(#{spaceBookDate}) IN (5,6)) AND
s.SPACE_USG_POSBL_BGN_TM <= #{spaceBookBgnTm} AND s.SPACE_USG_POSBL_END_TM >= #{spaceBookEndTm}
AND TIMESTAMPDIFF(HOUR, TIME(#{spaceBookBgnTm}), TIME(#{spaceBookEndTm})) <= s.SPACE_MAX_RSVD_TMS
, TRUE, FALSE)
FROM ( SELECT SPACE_WKEND_USG_POSBL_YN, SPACE_USG_POSBL_BGN_TM, SPACE_USG_POSBL_END_TM, SPACE_MAX_RSVD_TMS FROM space WHERE SPACE_NO = #{spaceBookSpaceNo}) s) AND DATEDIFF(#{spaceBookDate}, CURDATE()) >= 0
AND DATEDIFF(#{spaceBookDate}, CURDATE()) <= 90

mapper.update:

    SELECT EMPL_NO FROM empl WHERE EMPL_ID = #{emplId}

UPDATE space_book
SET SPACE_BOOK_STUS_CD = #{spaceBookStusCd}
                    , LAST_UPDR_IDNF_NO = #{spaceBookEmplNo}
                    , LAST_UPD_DTM = #{lastUpdDtm}
WHERE SPACE_BOOK_ID = #{spaceBookId}

    AND SPACE_BOOK_EMPL_NO = #{spaceBookEmplNo}


    SELECT EMPL_NO FROM empl WHERE EMPL_ID = #{emplId}

UPDATE space_book SET SPACE_BOOK_DATE = #{spaceBookDate}
                    , SPACE_BOOK_BGN_TM = #{spaceBookBgnTm}
                    , SPACE_BOOK_END_TM = #{spaceBookEndTm}
                    , SPACE_BOOK_CN = #{spaceBookCn}
                    , LAST_UPDR_IDNF_NO = #{spaceBookEmplNo}
                    , LAST_UPD_DTM = #{lastUpdDtm}
WHERE SPACE_BOOK_ID = #{spaceBookId}
    AND NOT EXISTS(
            SELECT 1
            FROM (
                SELECT *
                FROM space_book sb2
                WHERE sb2.SPACE_BOOK_DATE = #{spaceBookDate}
                    AND sb2.SPACE_BOOK_SPACE_NO = #{spaceBookSpaceNo}
                    AND SPACE_BOOK_STUS_CD = #{spaceBookStusCd}
                    AND sb2.SPACE_BOOK_ID != #{spaceBookId}
                    AND (
                        (sb2.SPACE_BOOK_END_TM > #{spaceBookBgnTm} AND sb2.SPACE_BOOK_BGN_TM < #{spaceBookEndTm})

                        OR
                        (sb2.SPACE_BOOK_END_TM = #{spaceBookBgnTm} AND sb2.SPACE_BOOK_EMPL_NO = #{spaceBookEmplNo})
                        OR
                        (sb2.SPACE_BOOK_BGN_TM = #{spaceBookEndTm} AND sb2.SPACE_BOOK_EMPL_NO = #{spaceBookEmplNo})

                    )
                 ) AS temp_sb2
            )
    AND (SELECT IF(

        NOT(s.SPACE_WKEND_USG_POSBL_YN = 'N'
        AND WEEKDAY(#{spaceBookDate}) IN (5,6))
        AND

        s.SPACE_USG_POSBL_BGN_TM <= #{spaceBookBgnTm}
        AND s.SPACE_USG_POSBL_END_TM >= #{spaceBookEndTm}

            AND TIMESTAMPDIFF(HOUR, TIME(#{spaceBookBgnTm}), TIME(#{spaceBookEndTm})) <= s.SPACE_MAX_RSVD_TMS

        , TRUE, FALSE)
FROM (
    SELECT SPACE_WKEND_USG_POSBL_YN, SPACE_USG_POSBL_BGN_TM, SPACE_USG_POSBL_END_TM, SPACE_MAX_RSVD_TMS
    FROM space
    WHERE SPACE_NO = #{spaceBookSpaceNo}) s)
        AND DATEDIFF(#{spaceBookDate}, CURDATE()) >= 0

        AND DATEDIFF(#{spaceBookDate}, CURDATE()) <= 90
