mapper.select:
SELECT s.SPACE_NO, s.SPACE_NM, s.SPACE_MAX_PSON_CNT, s.SPACE_LOC_DESC, s.SPACE_ADTN_DESC, s.SPACE_MAX_RSVD_TMS, s.SPACE_USG_POSBL_BGN_TM, s.SPACE_USG_POSBL_END_TM, s.SPACE_WKEND_USG_POSBL_YN, s.SPACE_HIDE_YN, r.RESC_NO, r.RESC_NM, f.FILE_NO, f.FILE_NM, f.FILE_ORGL_NM, f.FILE_TYP_NM, f.FILE_SIZE FROM (SELECT * FROM space WHERE SPACE_NO = #{spaceNo}
AND SPACE_HIDE_YN = 'N'
) s LEFT JOIN space_resc sr ON s.SPACE_NO = sr.SPACE_NO LEFT JOIN resc r ON sr.RESC_NO = r.RESC_NO LEFT JOIN file f ON s.SPACE_NO = f.ATCH_LOC_NO AND f.ATCH_LOC_CD = #{atchLocCd}

mapper.update:
UPDATE space SET SPACE_NM = #{spaceNm}, SPACE_MAX_PSON_CNT = #{spaceMaxPsonCnt}, SPACE_LOC_DESC = #{spaceLocDesc},
                 SPACE_ADTN_DESC=#{spaceAdtnDesc}, SPACE_MAX_RSVD_TMS=#{spaceMaxRsvdTms},
                 SPACE_USG_POSBL_BGN_TM=#{spaceUsgPosblBgnTm}, SPACE_USG_POSBL_END_TM=#{spaceUsgPosblEndTm},
                 SPACE_WKEND_USG_POSBL_YN=#{spaceWkendUsgPosblYn}, SPACE_HIDE_YN=#{spaceHideYn},
                 LAST_UPD_DTM=CURRENT_TIMESTAMP, LAST_UPDR_IDNF_NO=#{lastUpdrIdnfNo}
WHERE SPACE_NO = #{spaceNo}
